import { Component, OnInit } from '@angular/core';
import { FormControl, FormGroup } from '@angular/forms';
import { Subscription, interval, timer } from 'rxjs';
import { map, takeWhile } from 'rxjs/operators';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.scss']
})
export class AppComponent implements OnInit {
  title = 'test-technique-CACIB';
  euro:number = 1.1;
  changeForm = new FormGroup({
    tauxEuro: new FormControl(  { value: this.euro, disabled: true},),
    tauxUsd: new FormControl(''),
  });
  timerSubscription: Subscription; 

  constructor(){}
  ngOnInit(): void {
    this.timerSubscription = timer(0, 3000).pipe( 
      map(() => { 
        const max = this.euro + 0.05
        const min = this.euro - 0.05
        console.log(max);
        console.log(min);
        console.log(Math.floor(Math.random() * (max - min)) + min);

        this.euro = this.euro + Math.floor(Math.random() * (max - min)) + min;
        console.log(this.euro);

      }) 
    ).subscribe(); 

  }
}
<div class="card" style="margin: 5%;">
  <div class="card-header">
    Test taux de change
  </div>
  <div class="card-body">
    <blockquote class="blockquote mb-0">
      <form [formGroup]="changeForm">
        <div class="form-group">
          <label for="tauxEuro">Taux de change en Euro</label>
          <input type="number" formControlName="tauxEuro" class="form-control" id="tauxEuro" placeholder="taux de change">
        </div>
      </form>
    </blockquote>
  </div>
</div>
