import { Component, OnInit } from '@angular/core';
import { FormControl, FormGroup, Validators } from '@angular/forms';
import { Subscription, timer } from 'rxjs';
import { map } from 'rxjs/operators';
import { Conversion } from 'src/models/conversion';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.scss']
})
export class AppComponent implements OnInit {
  title = 'test-technique-CACIB';
  taux:number = 1.1;
  montant1:string = "EUR"
  montant2:string = "USD"
  changeForm = new FormGroup({
    tauxEuro: new FormControl(  { value: this.taux, disabled: true}),
    montantEuro: new FormControl('',[Validators.required]),
    montantUsd: new FormControl({ value:0, disabled: true}),
    forcedTaux: new FormControl(0),
  });
  timerSubscription: Subscription; 
  disableFix:boolean=false
  tableauHistorique : Conversion[]=[]


  ngOnInit(): void {
    this.setTimer()
  }

  setTimer(){
        this.timerSubscription = timer(0, 3000).pipe( 
      map(() => { 
        const max =  0.05
        const min =  -0.05        
        this.taux = +(this.taux + Math.random() * (max - min) + min).toFixed(3);        
        this.changeForm.setControl("tauxEuro",new FormControl({ value: this.taux, disabled: true}))
        if(this.changeForm.controls['montantEuro'].value){
          this.calculer()
        }
      }) 
    ).subscribe(); 
  }

  calculer(){
    const eur : number =+this.changeForm.controls['montantEuro'].value!;
    const result :number = +(eur * this.taux).toFixed(3)
    this.changeForm.setControl("montantUsd",new FormControl({ value: result, disabled: true}))

    let conversion: Conversion ={
      tauxReel: +this.changeForm.controls['tauxEuro'].value!,
      valeurInitial:+this.changeForm.controls['montantEuro'].value!,
      valeurFinal:+this.changeForm.controls['montantUsd'].value!,
      deviseInitial:this.montant1,
      deviseFinal:this.montant2,
      tauxSaisie:+this.changeForm.controls['forcedTaux'].value!,
    }
    if(this.tableauHistorique.length >= 5)
      this.tableauHistorique.shift()
      this.tableauHistorique.push(conversion)
  }

  switch(){
    let montant = this.montant1
    this.montant1 = this.montant2
    this.montant2 = montant
    this.taux = 1 / this.taux
  }

  fixTaux(){
    this.timerSubscription.unsubscribe()
    if(!this.check2percent()){
      if(this.changeForm.controls['forcedTaux'].value){
        this.taux = this.changeForm.controls['forcedTaux'].value
        this.changeForm.setControl("tauxEuro",new FormControl({ value: this.taux, disabled: true}))
      }
    }
    else{
      this.disableFix = true
      this.changeForm.setControl("forcedTaux",new FormControl({ value: 0, disabled: true}))
      this.setTimer()
    }
  }

  check2percent():boolean{
    let fixedTaux = +this.changeForm.controls['forcedTaux'].value!
    let variation = ((fixedTaux - this.taux)/this.taux)*100
    return variation > 2 || variation < -2
  }
}
<div class="card" style="margin: 5%;">
  <div class="card-header">
    Test taux de change
  </div>
  <div class="card-body">
    <blockquote class="blockquote mb-0">
      <form [formGroup]="changeForm">
        <div class="form-group">
          <label for="tauxEuro">Taux de change EUR/USD</label>
          <input type="number" formControlName="tauxEuro" class="form-control" id="tauxEuro" placeholder="taux de change">
        </div>
        <div class="form-group">
          <label for="montantEuro">Montant en {{montant1}}</label>
          <input type="number" formControlName="montantEuro" class="form-control" id="montantEuro" placeholder="Montant en {{montant1}}">
        </div>
        <div class="form-group">
          <label for="montantUsd">Montant en {{montant2}}</label>
          <input type="number" formControlName="montantUsd" class="form-control" id="montantUsd" placeholder="Montant en USD">
        </div>
        <div class="form-group">
          <label for="forcedTaux">Forcer le taux</label>
          <input type="number" formControlName="forcedTaux" class="form-control" id="forcedTaux" placeholder="Forcer le taux">
        </div>
        <span style="display:block;text-align: center;">        
          <button [disabled]="changeForm.invalid" type="submit" class="btn btn-primary" (click)="calculer()">Calculer</button>
          <button style="margin-left: 5px;"  type="submit" class="btn btn-primary" (click)="switch()">Switch</button>
          <button [disabled]="disableFix" style="margin-left: 5px;"  type="submit" class="btn btn-primary" (click)="fixTaux()">Fixer le taux</button></span>
      </form>
    </blockquote>
  </div>
  <table class="table">
    <thead>
      <tr>
        <th scope="col">Valeur initial</th>
        <th scope="col">Devise</th>
        <th scope="col">Valeur final</th>
        <th scope="col">Device</th>
        <th scope="col">Taux de change</th>
        <th scope="col">Taux saisie</th>
      </tr>
    </thead>
    <tbody>
      <tr *ngFor="let element of tableauHistorique">
        <td>{{element.valeurInitial}}</td>
        <td>{{element.deviseInitial}}</td>
        <td>{{element.valeurFinal}}</td>
        <td>{{element.deviseFinal}}</td>
        <td>{{element.tauxReel}}</td>
        <td>{{element.tauxSaisie}}</td>
      </tr>

    </tbody>
  </table>
</div>
